// Get cameras with reported data in the selected interval
from(bucket: "${bucket}")
  // Filter on time
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  // Filter using variable selectors (supports All and multi-select)
  |> filter(fn: (r) => r["area"] =~ /${area:regex}/)
  |> filter(fn: (r) => r["geography"] =~ /${geography:regex}/)
  |> filter(fn: (r) => r["region"] =~ /${region:regex}/)
  |> filter(fn: (r) => r["site"] =~ /${site:regex}/)
  |> filter(fn: (r) => r["type"] =~ /${type:regex}/)
  |> filter(fn: (r) => r["${unique_identifier}"] =~ /${device:regex}/)
  |> filter(fn: (r) => r["product_full_name"] =~ /${model:regex}/)
  // We cant flatten if the values are of different types, so we just
  // filter for an arbitrary value that we know exists...
  |> filter(fn: (r) =>
    r["_measurement"] == "cpu" and
    r["cpu"] == "cpu-total" and
    r["_field"] == "usage_idle"
  )
  // Group by device to merge to one time series per device
  |> group(columns: ["${unique_identifier}"])
  // Get the last data point for each device
  |> last()
  // Ungroup to prepare a flat output
  |> group()
  // Keep only timestamp and device identifier
  |> keep(columns: ["_time", "${unique_identifier}"])
  // Rename _time to seen
  |> rename(columns: {_time: "seen"})

