  |> rename(columns: {
    product_full_name: "product full name"
  })

from(bucket: "${bucket}")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)

  // Generic measurement just to access the tags. To reduce number of points,
  // we also filter to one single core and field.
  |> filter(fn: (r) =>
    r["_measurement"] == "cpu" and
    r["cpu"] == "cpu-total" and
    r["_field"] == "usage_idle" and

    // Only show the ones that are reporting firmware_version
    exists r["firmware_version"] and

    // Applied filters
    (r["area"] =~ /${area:regex}/) and
    (r["geography"] =~ /${geography:regex}/) and
    (r["region"] =~ /${region:regex}/) and
    (r["site"] =~ /${site:regex}/) and
    (r["type"] =~ /${type:regex}/) and
    (r["${unique_identifier}"] =~ /${device:regex}/) and
    (r["product_full_name"] =~ /${model:regex}/)
  )

  // Aggregate into time series windows to reduce number of returned data points
  |> aggregateWindow(every: 1h, fn: last, createEmpty: false)

  // Keep timestamp, device and firmware_version
  |> keep(columns: ["_time", "${unique_identifier}", "firmware_version"])

  // Rename and remap to Grafana-friendly _time, _field, _value,
  // that gets rid of the "firmware_version <host>" prefix in the visualization.
  |> map(fn: (r) => ({
    _time: r._time,
    _field: r["${unique_identifier}"],
    _value: r["firmware_version"]
  }))

  // Group per device (now in _field)
  |> group(columns: ["_field"])

  // Sort by time
  |> sort(columns: ["_time"])

from(bucket: "${bucket}")
  // Selected time span
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  // Filter based on drop-downs
  |> filter(fn: (r) => r["area"] =~ /${area:regex}/)
  |> filter(fn: (r) => r["geography"] =~ /${geography:regex}/)
  |> filter(fn: (r) => r["region"] =~ /${region:regex}/)
  |> filter(fn: (r) => r["site"] =~ /${site:regex}/)
  |> filter(fn: (r) => r["type"] =~ /${type:regex}/)
  |> filter(fn: (r) => r["${unique_identifier}"] =~ /${device:regex}/)
  |> filter(fn: (r) => r["product_full_name"] =~ /${model:regex}/)
  // Filter out the fields we are interested in
  |> filter(fn: (r) => r["_measurement"] == "cpu")
  |> filter(fn: (r) => r["cpu"] == "cpu-total")
  |> filter(fn: (r) => r["_field"] == "usage_idle")
  // One time series for each device
  |> group(columns: ["${unique_identifier}"])
  // Invert to CPU load instead of idle
  |> map(fn: (r) => ({ r with _value: 100.0 - r._value }))
  // Aggregate in blocks to reduce number of data points
  |> aggregateWindow(every: v.windowPeriod, fn: mean, createEmpty: false)

